/* Wrapper to hold both nav and main content */
.about-wrapper {
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: flex-start;
}

/* About Nav */
.about-nav {
    position: absolute; /* Use absolute to position relative to the nearest positioned ancestor */
    background-color: transparent;
    width: 200px;
    z-index: 1;
    top: 150px; /* Set top to position it higher */
    left: 2%; /* Move the left position further left (adjust as needed) */
    padding-top: 0;
}

/* Hide navbar on small screens */
@media screen and (max-width: 1200px), screen and (max-height: 480px) {
    .about-nav {
        display: none; /* Hides the navbar on small screens */
    }
}

/* Wrapper for the nav text to create indentation */
.about-nav-text-wrapper {
    position: relative; /* Keep it relative for the pseudo-element */
    padding-left: 10px; /* Adjust this value for the desired indentation */
}

/* Left red line styling - only for the text wrapper */
.about-nav-text-wrapper::before {
    content: '';
    position: absolute;
    left: 0; /* Position it to the left */
    top: 0; /* Start from the top */
    bottom: 0; /* Stretch to the bottom */
    width: 2px; /* Adjust width to make it thinner */
    background-color: #500000; /* Color of the red line */
    margin-left: 15px; /* Increase indent of the bar to match text */
}

/* Navbar title */
.about-NavBarTitle {
    font-family: 'Work Sans', sans-serif !important; 
    font-weight: 400 !important; 
    font-size: 16px !important; 
    color: black !important; 
    padding-left: 10px; /* Padding to align text */
    position: relative; /* Needed for the red line */
    text-decoration: none; /* Remove default underline */
}

/* Navbar text */
.about-NavBarText {
    font-family: 'Work Sans', sans-serif !important; 
    font-weight: 400 !important; 
    font-size: 16px !important; 
    color: black !important; 
    padding-left: 15px; /* Shift nav text 5px to the right */
    position: relative; /* Needed for the red line */
    text-decoration: none; /* Remove default underline */
    white-space: nowrap; /* Prevent text from wrapping */
    overflow: hidden; /* Hide any overflow text */
    text-overflow: ellipsis; /* Optional: Add ellipsis for overflow */
}

/* Hover effect */
.about-NavBarText:hover {
    text-decoration: underline !important; /* Underline text on hover */
}

/* Main Content Styling */
.about-main-content {
    flex-grow: 1; /* Allow the main content to take up remaining space */
    padding-left: 50px; /* Increase padding to create more space between nav and main content */
    padding-top: 20px; /* Ensure the content has top padding */
}

