/* Accordion Styles */
.aux-accordion {
  border: 2px solid #d1d1d1;
  border-radius: 10px;
  margin: 0 0.75rem 1.25rem;
}

.aux-accordion.focus {
  outline: solid #500000 0.2rem;
  border-color: #500000;
}

.aux-accordion:hover {
  border-color: #732f2f;
}

/* Updated Button Styles */
.aux-accordion > span button {
  background: transparent;
  border: none;
  text-align: left;
  cursor: pointer;
  padding: 1.5rem;
  display: flex;
  width: 100%;
  position: relative;
  align-items: center;
}

/* Extend clickable area to nested elements */
.aux-accordion-trigger {
  display: flex;
  align-items: center;
  width: 100%;
  padding: 0;
  background: none;
  border: none;
  cursor: pointer;
  position: relative;
}

.aux-accordion__title,
.ns-h3 {
  pointer-events: none; /* Pass clicks to the parent button */
  font-family: "Work Sans", Arial, sans-serif !important;
  font-weight: 700 !important; /* Bold weight */
  font-size: 24px !important;  /* Size for group titles */
}

/* Accordion Group Title - Specific h2 targeting */
.heading-group h2 {
  font-family: "Work Sans", Arial, sans-serif !important;
  font-weight: 700 !important;
  font-size: 24px !important;
  color: #000000 !important; /* New color for accordion group titles */
}



/* Content for expanded and collapsed states */
.aux-accordion > span button::after {
  position: absolute;
  right: 0;
  padding-right: 1.5rem;
  font-size: 1rem;
  font-weight: 300;
  font-style: italic;
  font-family: "Open Sans", Arial, sans-serif !important;
  color: #000000;
}

.aux-accordion > span button[aria-expanded="true"]::after {
  content: "Collapse -"; /* Show when expanded */
}

.aux-accordion > span button[aria-expanded="false"]::after {
  content: "Expand +"; /* Show when collapsed */
}

/* Accordion Titles */
.aux-accordion-trigger span {
  font-family: "Work Sans", Arial, sans-serif !important;
  font-weight: 600 !important;  /* Bold weight */
  font-size: 18px !important;   /* Size for accordion titles */
  color: #500000 !important; /* New color for accordion titles */
}

/* Accordion Descriptions */
.aux-accordion-panel p {
  font-family: "Open Sans", Arial, sans-serif !important;
  font-weight: 400 !important;  /* Regular weight */
  font-size: 16px !important;   /* Size for accordion descriptions */
  color: #3e3e3e !important; /* New color for accordion descriptions */
}

/* Slim Accordion Specific Styles */
.aux-accordion--publication,
.aux-accordion--slim,
.accordion-group--slim .accordion-container .aux-accordion {
  border: none;
  border-radius: 0;
  max-width: 1400px;
  margin-bottom: 0;
  transition: 0.1s ease;
  margin-left: 0.75rem;
  margin-right: 0.75rem;
  border-bottom: dotted #a7a7a7 2px;
}

.aux-accordion--publication.focus,
.aux-accordion--slim.focus,
.accordion-group--slim .accordion-container .aux-accordion.focus {
  outline: solid #500000 0.2rem;
  border-bottom: solid #500000 2px;
}

.aux-accordion--publication .aux-accordion-panel,
.aux-accordion--slim .aux-accordion-panel,
.accordion-group--slim .accordion-container .aux-accordion .aux-accordion-panel {
  margin: 0 1rem;
  padding: 1rem 0;
  border-top: solid 1px #d1d1d1;
}

.aux-accordion--publication > span,
.aux-accordion--slim > span,
.accordion-group--slim .accordion-container .aux-accordion > span {
  display: flex;
  align-items: center;
}

.aux-accordion--publication > span > h3,
.aux-accordion--publication > span h2,
.aux-accordion--slim > span > h3,
.accordion-group--slim .accordion-container .aux-accordion > span > h3,
.aux-accordion--slim > span h2,
.accordion-group--slim .accordion-container .aux-accordion > span h2 {
  width: 100%;
}

.aux-accordion--publication > span button,
.aux-accordion--slim > span button,
.accordion-group--slim .accordion-container .aux-accordion > span button {
  padding: 1rem;
}

.aux-accordion--publication > span button[aria-expanded=true]::after,
.aux-accordion--slim > span button[aria-expanded=true]::after,
.accordion-group--slim .accordion-container .aux-accordion > span button[aria-expanded=true]::after,
.aux-accordion--publication > span button[aria-expanded=false]::after,
.aux-accordion--slim > span button[aria-expanded=false]::after,
.accordion-group--slim .accordion-container .aux-accordion > span button[aria-expanded=false]::after {
  font-size: 0.95rem;
}

.aux-accordion--publication > span button .aux-accordion__title .ns-h3,
.aux-accordion--slim > span button .aux-accordion__title .ns-h3,
.accordion-group--slim .accordion-container .aux-accordion > span button .aux-accordion__title .ns-h3 {
