/* Configure header search button location, appearance, and disabling it in mobile mode */
.container .form-fieldset .form-field {
    display: flex; /* Aligns the input and button on the same line */
    align-items: center; /* Vertically centers both elements */
}

.container .form-fieldset .form-input {
    color: #000000; /* Sets the text color to black */
    border-radius: 0; /* Removes rounded corners */

}

.container .form-fieldset .search-button {
    font-family: 'Work Sans', sans-serif;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 9pt !important;
    width: auto;
    padding: 0 15px;
    height: 40px;
    background-color: #ffffff;
    color: #000000;
    border-radius: 0;
    border: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    line-height: normal;
    text-decoration: none;
    transition: background-color 0.3s ease;
    cursor: pointer;
    box-sizing: border-box;
    margin-left: 10px; 
}
@media (max-width: 800px) {
    .container .form-fieldset .search-button {
    display: none;

    }
}

