/* Re-enable all (tab) outling on site for accessability and set the color */
/* Show outline for keyboard navigation */
[contenteditable=true]:focus-visible, 
[href]:focus-visible, 
[tabindex]:not([tabindex="-1"]):focus-visible, 
button:focus-visible, 
details:focus-visible, 
input:focus-visible, 
select:focus-visible, 
textarea:focus-visible {
    outline: 2px solid #4b71fc !important; /* Customize the outline style as needed */
    outline-offset: 4px !important; /* Add spacing between the outline and the element */
}
/* Remove outline for mouse interactions */
[contenteditable=true]:focus:not(:focus-visible), 
[href]:focus:not(:focus-visible), 
[tabindex]:not([tabindex="-1"]):focus:not(:focus-visible), 
button:focus:not(:focus-visible), 
details:focus:not(:focus-visible), 
input:focus:not(:focus-visible), 
select:focus:not(:focus-visible), 
textarea:focus:not(:focus-visible) {
    outline: none !important; /* No outline for mouse click interactions */
}





