components/common/channel-selector:
{{#if theme_settings.channels.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a href="#"
           class="navUser-action navUser-action--channelSelector has-dropdown"
           data-dropdown="channelSelection"
           aria-controls="channelSelection"
           aria-expanded="false"
        >
            {{#each theme_settings.channels}}
                {{#if is_active}}
                    {{lang 'common.channel' code=code}}
                {{/if}}
            {{/each}}
            <i class="icon" aria-hidden="true">
                <svg>
                    <use href="#icon-chevron-down"/>
                </svg>
            </i>
        </a>
        <ul class="dropdown-menu" id="channelSelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each theme_settings.channels}}
                <li class="dropdown-menu-item">
                    <a href="{{{switch_url}}}" data-channel-code="{{{code}}}" data-warning="{{lang 'common.channel_switch_warning'}}">
                        {{#if is_active}}
                            <strong>{{name}}</strong>
                        {{else}}
                            {{name}}
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/channel-selector-mobil:
{{#if theme_settings.channels.length '>' 1}}
<a href="#"
   class="navPages-action has-subMenu"
   data-collapsible="navPages-channel"
   aria-controls="navPages-channel"
   aria-expanded="false"
   aria-label="
    {{#each theme_settings.channels}}
        {{#if is_active}}
            {{lang 'common.channel' code=code}}
        {{/if}}
    {{/each}}
   "
>
    {{#each theme_settings.channels}}
        {{#if is_active}}
            {{lang 'common.channel' code=code}}
        {{/if}}
    {{/each}}
    <i class="icon navPages-action-moreIcon" aria-hidden="true">
        <svg>
            <use href="#icon-chevron-down"></use>
        </svg>
    </i>
</a>
<div class="navPage-subMenu" id="navPages-channel" aria-hidden="true" tabindex="-1">
    <ul class="navPage-subMenu-list">
        {{#each theme_settings.channels}}
        <li class="navPage-subMenu-item">
            <a href="{{{switch_url}}}"
               class="navPage-subMenu-action navPages-action"
               aria-label="{{name}}"
               data-channel-code="{{{code}}}"
               data-warning="{{lang 'common.channel_switch_warning'}}"
            >
                {{name}}
            </a>
        </li>
        {{/each}}
    </ul>
</div>
{{/if}}

components/common/footer:
{{#if banners.bottom}}
    <div class="banners" data-banner-location="bottom">
        {{#each (limit banners.bottom_metadata 1)}}
            <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                <div data-event-type="promotion-click">
                    {{{this.content}}}
                </div>
            </div>
        {{/each}}
    </div>
{{/if}}
<footer class="footer" role="contentinfo">
    <h2 class="footer-title-sr-only">{{lang 'footer.title'}}</h2>
    <div class="container">
        {{#if theme_settings.social_icon_placement_bottom '!==' 'bottom_none'}}
            <article class="footer-info-col
                footer-info-col--social
                footer-info-col--{{#if theme_settings.social_icon_placement_bottom '===' 'bottom_left'}}left{{else}}right{{/if}}"
                data-section-type="footer-webPages">
                    <h3 class="footer-info-heading">{{lang 'social.connect'}}</h3>
                    {{> components/common/social-links}}
            </article>
        {{/if}}
        <section class="footer-info">
            <article class="footer-info-col footer-info-col--small" data-section-type="footer-webPages">
                <h3 class="footer-info-heading">{{lang 'footer.navigate'}}</h3>
                <ul class="footer-info-list">
                    {{#each pages}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li>
                        <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                    </li>
                </ul>
            </article>

            <article class="footer-info-col footer-info-col--small" data-section-type="footer-categories">
                <h3 class="footer-info-heading">{{lang 'footer.categories'}}</h3>
                <ul class="footer-info-list">
                    {{#each categories}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                </ul>
            </article>

            {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
            <article class="footer-info-col footer-info-col--small" data-section-type="footer-brands">
                <h3 class="footer-info-heading">{{lang 'footer.brands'}}</h3>
                <ul class="footer-info-list">
                    {{#each shop_by_brand}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                </ul>
            </article>
            {{/and}}

            {{#if settings.address}}
            <article class="footer-info-col footer-info-col--small" data-section-type="storeInfo">
                <h3 class="footer-info-heading">{{lang 'footer.info'}}</h3>
                <address>{{nl2br settings.address}}</address>
                {{#if settings.phone_number}}
                    <strong>{{lang 'footer.call_us' phone_number=settings.phone_number}}</strong>
                {{/if}}
            </article>
            {{/if}}

            <article class="footer-info-col" data-section-type="newsletterSubscription">
                {{#if settings.show_newsletter_box}}
                    {{> components/common/subscription-form}}
                {{/if}}
                {{> components/common/payment-icons}}
            </article>
        </section>
        {{#if theme_settings.show_powered_by}}
            <div class="footer-copyright">
                <p class="powered-by">{{lang 'footer.powered_by'}} <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow">BigCommerce</a></p>
            </div>
        {{/if}}
        {{#if theme_settings.show_copyright_footer}}
            <div class="footer-copyright">
                <p class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}} </p>
            </div>
        {{/if}}
    </div>
    {{{region name="ssl_site_seal--global"}}}
</footer>

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}"
       href="{{url}}"
       data-collapsible="navPages-{{id}}"
       aria-label="{{name}}"
    >
        {{name}}
        <i class="icon navPages-action-moreIcon" aria-hidden="true">
            <svg><use href="#icon-chevron-down" /></svg>
        </i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}"
       href="{{url}}"
       aria-label="{{name}}"
    >
        {{name}}
    </a>
{{/if}}

pages/account/return-saved:
